pragma solidity ^0.8.10;

contract Piedra {
    //assign Token contract to variable
    event TrasferEmmitedAddress(address indexed sender, address value);
    event TrasferEmmited(address indexed sender, uint256 value);
    uint8 PIEDRA = 1;
    uint8 PAPEL = 2;
    uint8 TIJERA = 3;

    address jugador1;
    uint8 opcionJugador1 = 0; //piedra, papel, tijera 1 ,2 ,3 

    address jugador2; 
    uint8 opcionJugador2 = 0;

    function getOpcionJugador1() public view returns (uint8) {
        return opcionJugador1;
    }

    function getOpcionJugador2() public view returns (uint8) {
        return opcionJugador2;
    }

    function jugar(uint8 opcion) payable public {
        require(msg.value == 0.02 ether, "Insuficient amount of BNB");

        if( opcionJugador1 == 0){
            jugador1 = msg.sender;
            opcionJugador1 = opcion;

        } else if (opcionJugador2 == 0){
            jugador2 = msg.sender;
            opcionJugador2 = opcion;

        } else {

            // resolvemos
            if( (opcionJugador1 == PIEDRA && opcionJugador2 == TIJERA) ||
                (opcionJugador1 == PAPEL && opcionJugador2 == PIEDRA) ||
                (opcionJugador1 == TIJERA && opcionJugador2 == PAPEL) ){
            
                payable(jugador1).transfer( 0.035 ether );
            
            } else if ( (opcionJugador2 == PIEDRA && opcionJugador1 == TIJERA) ||
                (opcionJugador2 == PAPEL && opcionJugador1 == PIEDRA) ||
                (opcionJugador2 == TIJERA && opcionJugador1 == PAPEL) ){

                payable(jugador2).transfer( 0.035 ether );
            } else {
                // nadie gana
                payable(jugador1).transfer( 0.015 ether  );
                payable(jugador2).transfer( 0.015 ether  );
            }
            // reiniciamos
            //jugador1 = address(0);
            //opcionJugador1 = 0;
            //jugador2 = address(0);
            opcionJugador2 = 0;

            //Juega el 1
            jugador1 = msg.sender;
            opcionJugador1 = opcion;
        }
    }
}
